  <tr>
    <th>Name</th>
    <th>Price</th>
    <th>Count</th>
    <th> Total</th>

   
  </tr>;
  {
    this.state.productsCart.map((e,i) => (
       <tr key = {i} >   
      <td>e.name</td>
      <td>e.price</td>
      <td>e.count</td>
      <td>e.price*e.count</td>
      </tr>
    )   )
  }
  </table>
}

