import React  from 'react';

import './App.css';
import Login  from './components/Login/Login.js';
//import UsersProduct from './components/UsersProduct/UsersProduct.js';
 import Registration from './components/Registration/Registeration.js';
 import Home from './components/Home/Home.js';


 import {
   BrowserRouter as Router,
 Switch,
 Link,
  Route,
  Redirect,
} from "react-router-dom"
 class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {

    };
  }
  setUserAuth = (value) => this.setState({ isAuthenticated: true });
  render() {
    return (
      <div className="app">

        <Router>
          <Switch>
            <Route path="/auth/Login ">
              <Login setUserAuth={this.setUserAuth} />
            </Route>
            <Route exact path="/auth/reg">
              <Registration setUserAuth={this.setUserAuth} />
            </Route>
            <PrivateRoute isAuthenticated={this.state.isAuthenticated} path="/">
           < Home/>
            </PrivateRoute>

          </Switch>
        </Router>
      </div>
    );
  }
}
function PrivateRoute({ children, isAuthenticated, ...rest }) {
  return (
    <Route
      {...rest}
      render={({ location }) =>
        isAuthenticated ? (
          children
        ) : (
          <Redirect
            to={{
              pathname: "/auth/login",
              state: { from: location },
            }}
          />
        )
      }
    />
  );
}

export default App;
