      count: 0,
      total: "",
      productsCart: null,
    };
  }

  componentDidMount() {
    axios.get(`http://localhost:4000/products`).then((res) => {
      const productsCart = res.data;
      console.log(productsCart);
      this.setState({ productsCart });
    });
  }

  

  handelChange(e) {
    this.setState({
      [e.target.name]: e.target.value,
    });
  }

  handelSubmite(e) {
    e.preventDefault();
