      const productsCart = res.data;
      this.setState({ productsCart: productsCart });
    });
  }

  render() {
    console.log(this.state.productsCart);

    return (
      <div className="Cart">
        {this.state.productsCart ? (
          <Products productsCart={this.state.productsCart} />
        ) : null}
      </div>
    );
  }
}

export default Cart;
